commits.content:
// element-ui 样式覆盖
.el-button {
  &--primary {
    background-color: $--color-primary;
  }
}

// 表单样式统一
.el-form {
  .el-form-item {
    margin-bottom: 22px;

    &__label {
      padding-right: 12px;
      font-weight: normal;
    }
  }
}

// 弹窗统一样式
.el-dialog {
  border-radius: $--border-radius-base;

  &__header {
    padding: 20px;
    border-bottom: 1px solid $--border-color-lighter;
  }

  &__body {
    padding: 30px 20px;
  }

  &__footer {
    padding: 20px;
    border-top: 1px solid $--border-color-lighter;
    text-align: right;
  }
}

// 表格统一样式
.el-table {
  th {
    background-color: #F5F7FA;
    color: $--color-text-regular;
    font-weight: 500;
  }

  td {
    padding: 12px 0;
  }
}

// 分页统一样式
.el-pagination {
  margin-top: 20px;
  text-align: right;
}
